mapper.select:
	SELECT
		COUNT(*) AS CNT
	FROM COM_MEMBER
	WHERE MEMBER_ID = #{USER_ID}
    AND USERTYPE = #{USERTYPE}
    AND DEL_YN = 'N'

	SELECT FAIL_CNT 
    FROM COM_MEMBER
    WHERE DEL_YN='N'
	    AND MEMBER_ID= #{USER_ID}
	    AND USERTYPE= #{USERTYPE}

mapper.update:
UPDATE COM_MEMBER SET
	FAIL_CNT= ISNULL(FAIL_CNT, 0)+1
WHERE DEL_YN='N'
    AND MEMBER_ID= #{USER_ID}
    AND USERTYPE= #{USERTYPE}

UPDATE COM_MEMBER SET
	FAIL_CNT= 0
WHERE DEL_YN='N'
    AND MEMBER_ID= #{USER_ID}
    AND USERTYPE= #{USERTYPE}

mapper.insert:
INSERT INTO IP_INFO(
	   CONTRY
	,  IP_ADDR
	, CONN_DATE
)VALUES(
	  #{CONTRY}
	, #{IP_ADDR}
	, NOW()
)
