mapper.insert:

	SELECT LAST_INSERT_ID() AS MEM_SEQ


INSERT INTO COM_USER_MEMBER (
	  MEMBER_ID
	, MEMBER_PWD
	, MEMBER_NICK
	, MEMBER_NM
	, MEMBER_EMAIL
	, RET_YN
	, REGDATE
	, UPDDATE
)VALUES(
	  #{MEMBER_ID}
	, MD5(#{MEMBER_PWD})
	, #{MEMBER_NICK}
	, #{MEMBER_NM}
	, #{MEMBER_EMAIL}
	, 'N'
	, NOW()
	, NOW()
)

mapper.select:
SELECT
	MEMBER_ID
FROM COM_USER_MEMBER
WHERE 1 = 1
AND MEMBER_NM = #{MEMBER_NM}
AND MEMBER_NICK = #{MEMBER_NICK}

SELECT
	MEMBER_EMAIL
FROM COM_USER_MEMBER
WHERE 1 = 1
AND MEMBER_ID = #{MEMBER_ID}
AND MEMBER_NM = #{MEMBER_NM}
AND MEMBER_EMAIL = #{MEMBER_EMAIL}

mapper.insert:
INSERT INTO COM_SEND_MAIL_HIST(
	  TO_MAIL_ID
	, REGDATE
)VALUES(
	  #{TO_MAIL_ID}
	, NOW()
)

mapper.update:
UPDATE COM_USER_MEMBER SET
	  MEMBER_NICK = #{MEMBER_NICK}
	, MEMBER_EMAIL = #{MEMBER_EMAIL}
	, UPDDATE = NOW()
WHERE 1 = 1
AND MEMBER_ID = #{SESSION_USR_ID}
