mapper.select:
WITH MENU AS (SELECT TBLA.MENU_CODE, REPLICATE('',(TBLA.MENU_LEVL-1)*2 - 1) + TBLA.MENUNM AS MENUNM, TBLA.EXPS_ORDR, TBLA.USE_YN, TBLA.DLTE_YN, TBLA.MENU_URL, TBLA.USER_DVID, TBLA.MENU_LEVL, TBLA.MENU_EXPL, TBLA.IMG2_URL, TBLA.IMG1_URL, TBLA.TARGET_LOCT, TBLA.PRTCODE, TBLA.TITLE_IMG_URL, CONVERT(VARCHAR(255),'0') AS SORT, CASE WHEN TBLA.MENU_LEVL = '1' THEN MENUNM WHEN TBLA.MENU_LEVL = '2' THEN ((SELECT MENUNM FROM COM_MENU WHERE MENU_CODE = TBLA.PRTCODE)+' - '+TBLA.MENUNM) WHEN TBLA.MENU_LEVL = '3' THEN (SELECT MENUNM FROM COM_MENU WHERE MENU_CODE = (SELECT PRTCODE FROM COM_MENU WHERE MENU_CODE = TBLA.PRTCODE))+' - '+(SELECT MENUNM FROM COM_MENU WHERE MENU_CODE = TBLA.PRTCODE)+' - '+TBLA.MENUNM END MENU_NAVI FROM COM_MENU TBLA WHERE TBLA.PRTCODE = '' UNION ALL SELECT TBLB.MENU_CODE, CASE WHEN TBLB.MENU_LEVL = 1 THEN REPLICATE('',(TBLB.MENU_LEVL-1)*2) + TBLB.MENUNM ELSE REPLICATE('',(TBLB.MENU_LEVL-1)*2 - 1) + ' ' + TBLB.MENUNM END AS MENUNM, TBLB.EXPS_ORDR, TBLB.USE_YN, TBLB.DLTE_YN, TBLB.MENU_URL, TBLB.USER_DVID, TBLB.MENU_LEVL, TBLB.MENU_EXPL, TBLB.IMG2_URL, TBLB.IMG1_URL, TBLB.TARGET_LOCT, TBLB.PRTCODE, TBLB.TITLE_IMG_URL, CONVERT(VARCHAR(255), RTRIM(TBLC.SORT) + '/' + REPLACE(STR(TBLB.EXPS_ORDR,3),' ','0')) AS SORT, CASE WHEN TBLB.MENU_LEVL = '1' THEN TBLB.MENUNM WHEN TBLB.MENU_LEVL = '2' THEN ((SELECT MENUNM FROM COM_MENU WHERE MENU_CODE = TBLB.PRTCODE)+' - '+TBLB.MENUNM) WHEN TBLB.MENU_LEVL = '3' THEN (SELECT MENUNM FROM COM_MENU WHERE MENU_CODE = (SELECT PRTCODE FROM COM_MENU WHERE MENU_CODE = TBLB.PRTCODE))+' - '+(SELECT MENUNM FROM COM_MENU WHERE MENU_CODE = TBLB.PRTCODE)+' - '+TBLB.MENUNM END MENU_NAVI FROM COM_MENU TBLB JOIN MENU TBLC ON TBLB.PRTCODE = TBLC.MENU_CODE ) SELECT * FROM MENU WHERE DLTE_YN='N' AND MENU_CODE !='ROOT'
AND USER_DVID = #{USER_DVID}
ORDER BY SORT OPTION (MAXRECURSION 5)

SELECT A.AUTHCODE ROLL
FROM TB_AUTHGRUP_USER B , TB_AUTHGRUP A
WHERE A.DLTE_YN ='N' AND B.AUTH_CODE = A.AUTHCODE AND B.AUTH_USER_NUM = #{USRID}
