INSERT INTO COM_BOARD_INFO(
	  TITLE
	, BOARD_TYPE
	, CONTENTS
	, VIEW_CNT    
	, IP       
	, REG_NICK 
	, SECRET_YN
	, REP_SEQ
	, SORT_NUM
	, REG_ID    
	, REGDATE     
	, UPD_ID
	, UPDDATE
)VALUES(
	  #{TITLE}
	, #{BOARD_TYPE}
	, #{CONTENTS}
	, 0    
	, #{IP}       
	, #{SESSION_USER_NICK} 
	, #{SECRET_YN}
	, 0
	, 0
	, #{SESSION_USER_ID}    
	, NOW()     
	, #{SESSION_USER_ID}    
	, NOW()
)

   SELECT LAST_INSERT_ID() ANSWER_BOARD_SEQ

mapper.update:
UPDATE COM_BOARD_INFO SET
	  TITLE = #{TITLE}
	, CONTENTS = #{CONTENTS}
	, UPD_ID = #{SESSION_USER_ID}
	, SECRET_YN = #{SECRET_YN}
	, UPDDATE = NOW()
WHERE BOARD_SEQ = #{BOARD_SEQ}

UPDATE COM_BOARD_INFO SET
	  CONTENTS = #{CONTENTS}
	, UPD_ID = #{SESSION_USER_ID}
	, UPDDATE = NOW() 
WHERE BOARD_SEQ = #{ANSWER_BOARD_SEQ}

UPDATE COM_BOARD_INFO SET
	VIEW_CNT = VIEW_CNT + 1
WHERE BOARD_SEQ = #{BOARD_SEQ}

mapper.delete:
UPDATE COM_BOARD_INFO SET
	DEL_YN = 'Y'
WHERE BOARD_SEQ = #{BOARD_SEQ}

mapper.update:
UPDATE COM_BOARD_INFO SET
	  REP_YN = 'Y'
	, REP_SEQ = #{ANSWER_BOARD_SEQ}
WHERE BOARD_SEQ = #{BOARD_SEQ}

