mapper.select:
SELECT
	COUNT(*)AS TOTAL_CNT
FROM TB_WARRANT_INFO
WHERE 1 = 1
AND APT_CODE = #{SESSION_APT_CODE}

	AND DANZI = #{SCH_DANZI}


	AND DONG = #{SCH_DONG}


	AND HOSU = #{SCH_HOSU}


	AND WARRANT_YN = #{SCH_WARRANT_YN}


	AND AMT_YN = #{SCH_AMT_YN}


	AND(
		DONG LIKE CONCAT('%', #{SCH_WORD},'%')
		OR
		HOSU LIKE CONCAT('%', #{SCH_WORD},'%')
	)

SELECT TBA.* FROM( SELECT TB.* , CAST(CEILING((RNUM)/#{PAGE_SIZE}) AS CHAR) AS PAGE , CAST(#{TOTAL_CNT} - RNUM +1 AS CHAR) AS RNUMUM FROM( SELECT T.* , @RNUM := @RNUM + 1 AS RNUM FROM( SELECT WARRANT_SEQ , APT_CODE , DANZI , DONG , HOSU , USER_NM , NICK , HP , HP2 , USER_NM2 , WARRANT_YN , CASE WHEN WARRANT_YN = 'Y' THEN '제출' ELSE '미제출' END AS WARRANT_YN_NM , AMT_YN , CASE WHEN AMT_YN = 'Y' THEN '납부' ELSE '미납부' END AS AMT_YN_NM , ADDR , JOHAP_YN , CASE WHEN JOHAP_YN = 'Y' THEN '조합' ELSE '일반분양자' END AS JOHAP_YN_NM , ETC , AMT , AMT_IN_DATE , DATE_FORMAT(REGDATE, '%Y-%m-%d') REGDATE , DATE_FORMAT(UPDDATE, '%Y-%m-%d') UPDDATE FROM TB_WARRANT_INFO WHERE 1 = 1 AND APT_CODE = #{SESSION_APT_CODE}
AND DANZI = #{SCH_DANZI}
AND DONG = #{SCH_DONG}
AND HOSU = #{SCH_HOSU}
AND WARRANT_YN = #{SCH_WARRANT_YN}
AND AMT_YN = #{SCH_AMT_YN}
AND( DONG LIKE CONCAT('%', #{SCH_WORD},'%') OR HOSU LIKE CONCAT('%', #{SCH_WORD},'%') )
ORDER BY CAST(DONG AS UNSIGNED), CAST(HOSU AS UNSIGNED)
)T,(SELECT @RNUM := 0 ) STR )TB )TBA WHERE PAGE = #{CURR_PAGE} ORDER BY CAST(DONG AS UNSIGNED), CAST(HOSU AS UNSIGNED)

SELECT
	DONG	
FROM TB_WARRANT_INFO
WHERE 1 = 1
AND APT_CODE = #{SESSION_APT_CODE}

	AND DANZI = #{SCH_DANZI}


	AND DANZI = #{DANZI}

GROUP BY DONG
ORDER BY CONVERT(DONG ,UNSIGNED)
LIMIT 9999999

SELECT
	HOSU	
FROM TB_WARRANT_INFO
WHERE 1 = 1
AND APT_CODE = #{SESSION_APT_CODE}

	AND DONG = #{SCH_DONG}


	AND DONG = #{DONG}

ORDER BY CONVERT(HOSU ,UNSIGNED)
LIMIT 9999999

SELECT DONG FROM TB_WARRANT_INFO WHERE 1 = 1 AND APT_CODE = #{SESSION_APT_CODE}
AND DANZI = #{DANZI}
GROUP BY DONG ORDER BY CONVERT(DONG ,UNSIGNED) LIMIT 9999999

SELECT HOSU FROM TB_WARRANT_INFO WHERE 1 = 1 AND APT_CODE = #{SESSION_APT_CODE}
AND DONG = #{DONG}
ORDER BY CONVERT(HOSU ,UNSIGNED) LIMIT 9999999

