mapper.select:
	SELECT
		COUNT(*) AS TOTAL_CNT
	FROM(
		SELECT
			(SELECT COUNT(*) FROM TB_WARRANT_HIST WHERE WARRANT_SEQ = A.WARRANT_SEQ AND APT_CODE = A.APT_CODE AND DANZI = A.DANZI AND DONG = A.DONG AND HOSU= A.HOSU AND USER_NM != A.USER_NM) AS USER_NM_CNT
		  , (SELECT COUNT(*) FROM TB_WARRANT_HIST WHERE WARRANT_SEQ = A.WARRANT_SEQ AND APT_CODE = A.APT_CODE AND DANZI = A.DANZI AND DONG = A.DONG AND HOSU= A.HOSU AND HP != A.HP) AS HP_CNT
		  , (SELECT COUNT(*) FROM TB_WARRANT_HIST WHERE WARRANT_SEQ = A.WARRANT_SEQ AND APT_CODE = A.APT_CODE AND DANZI = A.DANZI AND DONG = A.DONG AND HOSU= A.HOSU AND WARRANT_YN != A.WARRANT_YN) AS WARRANT_YN_CNT
		  , (SELECT COUNT(*) FROM TB_WARRANT_HIST WHERE WARRANT_SEQ = A.WARRANT_SEQ AND APT_CODE = A.APT_CODE AND DANZI = A.DANZI AND DONG = A.DONG AND HOSU= A.HOSU AND AMT_YN != A.AMT_YN) AS AMT_YN_CNT
		  , (SELECT COUNT(*) FROM TB_WARRANT_HIST WHERE WARRANT_SEQ = A.WARRANT_SEQ AND APT_CODE = A.APT_CODE AND DANZI = A.DANZI AND DONG = A.DONG AND HOSU= A.HOSU AND ADDR != A.ADDR) AS ADDR_CNT
		FROM TB_WARRANT_INFO A INNER JOIN TB_WARRANT_HIST B ON A.WARRANT_SEQ = B.WARRANT_SEQ
                                AND A.APT_CODE = B.APT_CODE AND A.DANZI = B.DANZI AND A.DONG = B.DONG AND A.HOSU = B.HOSU
		WHERE 1 = 1
		AND A.APT_CODE = #{SESSION_APT_CODE}

			AND A.DANZI = #{SCH_DANZI}


			AND A.DONG = #{SCH_DONG}


			AND A.HOSU = #{SCH_HOSU}


			AND A.WARRANT_YN = #{SCH_WARRANT_YN}


			AND AMT_YN = #{SCH_AMT_YN}


			AND(
				A.DONG LIKE CONCAT('%', #{SCH_WORD},'%')
				OR
				A.HOSU LIKE CONCAT('%', #{SCH_WORD},'%')
			)

		GROUP BY A.WARRANT_SEQ, A.APT_CODE, A.DANZI, A.DONG, A.HOSU, A.UPDDATE 
	)T
	WHERE (USER_NM_CNT + HP_CNT + WARRANT_YN_CNT + AMT_YN_CNT + ADDR_CNT) > 0

SELECT TBA.* FROM( SELECT TB.* , CAST(CEILING((RNUM)/#{PAGE_SIZE}) AS CHAR) AS PAGE , CAST(#{TOTAL_CNT} - RNUM +1 AS CHAR) AS RNUMUM FROM( SELECT T.* , @RNUM := @RNUM + 1 AS RNUM FROM( SELECT A.WARRANT_SEQ , A.APT_CODE , A.DANZI , A.DONG , A.HOSU , (SELECT COUNT(*) FROM TB_WARRANT_HIST WHERE WARRANT_SEQ = A.WARRANT_SEQ AND APT_CODE = A.APT_CODE AND DANZI = A.DANZI AND DONG = A.DONG AND HOSU= A.HOSU AND USER_NM != A.USER_NM) AS USER_NM_CNT , (SELECT COUNT(*) FROM TB_WARRANT_HIST WHERE WARRANT_SEQ = A.WARRANT_SEQ AND APT_CODE = A.APT_CODE AND DANZI = A.DANZI AND DONG = A.DONG AND HOSU= A.HOSU AND HP != A.HP) AS HP_CNT , (SELECT COUNT(*) FROM TB_WARRANT_HIST WHERE WARRANT_SEQ = A.WARRANT_SEQ AND APT_CODE = A.APT_CODE AND DANZI = A.DANZI AND DONG = A.DONG AND HOSU= A.HOSU AND WARRANT_YN != A.WARRANT_YN) AS WARRANT_YN_CNT , (SELECT COUNT(*) FROM TB_WARRANT_HIST WHERE WARRANT_SEQ = A.WARRANT_SEQ AND APT_CODE = A.APT_CODE AND DANZI = A.DANZI AND DONG = A.DONG AND HOSU= A.HOSU AND AMT_YN != A.AMT_YN) AS AMT_YN_CNT , (SELECT COUNT(*) FROM TB_WARRANT_HIST WHERE WARRANT_SEQ = A.WARRANT_SEQ AND APT_CODE = A.APT_CODE AND DANZI = A.DANZI AND DONG = A.DONG AND HOSU= A.HOSU AND ADDR != A.ADDR) AS ADDR_CNT , DATE_FORMAT(A.UPDDATE, '%Y-%m-%d') UPDDATE FROM TB_WARRANT_INFO A INNER JOIN TB_WARRANT_HIST B ON A.WARRANT_SEQ = B.WARRANT_SEQ AND A.APT_CODE = B.APT_CODE AND A.DANZI = B.DANZI AND A.DONG = B.DONG AND A.HOSU = B.HOSU WHERE 1 = 1 AND A.APT_CODE = #{SESSION_APT_CODE}
AND A.DANZI = #{SCH_DANZI}
AND A.DONG = #{SCH_DONG}
AND A.HOSU = #{SCH_HOSU}
AND A.WARRANT_YN = #{SCH_WARRANT_YN}
AND AMT_YN = #{SCH_AMT_YN}
AND( A.DONG LIKE CONCAT('%', #{SCH_WORD},'%') OR A.HOSU LIKE CONCAT('%', #{SCH_WORD},'%') )
GROUP BY A.WARRANT_SEQ, A.APT_CODE, A.DANZI, A.DONG, A.HOSU, A.UPDDATE ORDER BY CAST(A.DONG AS UNSIGNED), CAST(A.HOSU AS UNSIGNED)
)T,(SELECT @RNUM := 0 ) STR WHERE (USER_NM_CNT + HP_CNT + WARRANT_YN_CNT + AMT_YN_CNT + ADDR_CNT) > 0 )TB )TBA WHERE PAGE = #{CURR_PAGE} ORDER BY CAST(DONG AS UNSIGNED), CAST(HOSU AS UNSIGNED)

SELECT
	  COUNT(*)AS TOTAL_CNT
FROM TB_AMT_INFO A LEFT OUTER JOIN TB_WARRANT_INFO B ON A.APT_CODE = B.APT_CODE AND A.DONG = B.DONG AND A.HOSU = B.HOSU
WHERE 1 = 1
AND A.APT_CODE = #{SESSION_APT_CODE}

		AND B.DANZI = #{SCH_DANZI}


		AND B.DONG = #{SCH_DONG}


		AND B.HOSU = #{SCH_HOSU}


		AND B.WARRANT_YN = #{SCH_WARRANT_YN}


		AND B.AMT_YN = #{SCH_AMT_YN}


		AND(
			A.USER_NM LIKE CONCAT('%', #{SCH_WORD},'%')
			OR
			A.USER_NM LIKE CONCAT('%', #{SCH_WORD},'%')
		)

SELECT TBA.* FROM( SELECT TB.* , CAST(CEILING((RNUM)/#{PAGE_SIZE}) AS CHAR) AS PAGE , CAST(#{TOTAL_CNT} - RNUM +1 AS CHAR) AS RNUMUM FROM( SELECT T.* , @RNUM := @RNUM + 1 AS RNUM FROM( SELECT A.AMT_SEQ , B.DONG , B.HOSU , A.USER_NM , A.AMT_IN_DATE , A.AMT , A.REGDATE FROM TB_AMT_INFO A LEFT OUTER JOIN TB_WARRANT_INFO B ON A.APT_CODE = B.APT_CODE AND A.DONG = B.DONG AND A.HOSU = B.HOSU WHERE 1 = 1 AND A.APT_CODE = #{SESSION_APT_CODE}
AND B.DANZI = #{SCH_DANZI}
AND B.DONG = #{SCH_DONG}
AND B.HOSU = #{SCH_HOSU}
AND B.WARRANT_YN = #{SCH_WARRANT_YN}
AND B.AMT_YN = #{SCH_AMT_YN}
AND( A.USER_NM LIKE CONCAT('%', #{SCH_WORD},'%') OR A.USER_NM LIKE CONCAT('%', #{SCH_WORD},'%') )
ORDER BY A.REGDATE DESC
)T,(SELECT @RNUM := 0 ) STR )TB )TBA WHERE PAGE = #{CURR_PAGE} ORDER BY REGDATE DESC

