mapper.insert:
INSERT INTO COM_FREE_BOARD(
	  TITLE
	, CONTENT
	, DEL_YN
	, VIEW_CNT    
	, ATTACH_YN   
	, IP       
	, DEVICE_TYPE 
	, REG_NICK 
	, REGID    
	, REGDATE     
	, UPDDATE
)VALUES(
	  #{TITLE}
	, #{CONTENTS}
	, 'N'
	, 0   
	, #{ATTACH_YN}   
	, #{IP}       
	, #{DEVICE_TYPE} 
	, #{SESSION_USR_NICK} 
	, #{SESSION_USR_ID}    
	, NOW()    
	, NOW()
)

mapper.update:
UPDATE COM_FREE_BOARD SET
	  TITLE = #{TITLE}
	, CONTENT = #{CONTENTS}
	, UPDDATE = NOW()
WHERE 1 = 1
AND BOARD_SEQ = #{BOARD_SEQ}

mapper.insert:

   SELECT LAST_INSERT_ID() RIP_SEQ


INSERT INTO COM_FREE_BOARD_RIP(
	  BOARD_SEQ 
    , RIP_RIP_SEQ	
    , SORT 
    , RIP_COMMENT 
    , DEL_YN 
    , DEVICE_TYPE 
    , IP 
    , REG_NICK 
    , REGID 
    , REGDATE 
    , UPDDATE 
)VALUES(
	  #{BOARD_SEQ} 
    , #{RIP_RIP_SEQ}
    , #{SORT}
    , #{RIP_COMMENT} 
    , 'N'
	, #{DEVICE_TYPE} 
    , #{IP}       
	, #{SESSION_USR_NICK}
    , #{SESSION_USR_ID} 
    , NOW()
    , NOW()
)

mapper.update:
UPDATE COM_FREE_BOARD_RIP SET
	RIP_RIP_SEQ = #{RIP_RIP_SEQ}
WHERE 1 = 1
AND RIP_SEQ = #{RIP_SEQ}

mapper.delete:
UPDATE COM_FREE_BOARD_RIP SET
	  DEL_YN = 'Y'
	, UPDDATE = NOW()
WHERE 1 = 1
AND BOARD_SEQ = #{BOARD_SEQ} 
AND RIP_RIP_SEQ = #{RIP_RIP_SEQ}
AND SORT = #{SORT}

