mapper.select:
SELECT COUNT(*) AS TOTAL_CNT FROM TB_ILLUMI_MEET_DTL WHERE 1 = 1 AND MEET_SEQ = #{MEET_SEQ}
AND ( DES_NM LIKE CONCAT('%', #{SCH_WORD},'%') OR NICK LIKE CONCAT('%', #{SCH_WORD},'%') )
AND DES_YN = #{SCH_DES_YN}

SELECT TB.* FROM( SELECT T.* , CEILING((RNUM)/#{PAGE_SIZE}) AS PAGE , CAST(#{TOTAL_CNT} - RNUM +1 AS CHAR) AS RNUMUM FROM( SELECT MEET_DTL_SEQ , MEET_SEQ , NICK , ADULT_CNT , ELE_CNT , INF_CNT , TARGET_CNT , FORMAT(DES_PLAN_AMT, 0)AS DES_PLAN_AMT , DES_NM , FORMAT(DES_AMT, 0)AS DES_AMT , DES_DATE , DES_YN , CASE WHEN DES_YN = 'Y' THEN '입금' ELSE '미입금' END AS DES_YN_NM , REFUND_YN , REFUND_DATE , @RNUM := @RNUM + 1 AS RNUM FROM TB_ILLUMI_MEET_DTL ,(SELECT @RNUM := 0 ) STR WHERE 1 = 1 AND MEET_SEQ = #{MEET_SEQ}
AND ( DES_NM LIKE CONCAT('%', #{SCH_WORD},'%') OR NICK LIKE CONCAT('%', #{SCH_WORD},'%') )
AND DES_YN = #{SCH_DES_YN}
ORDER BY MEET_DTL_SEQ DESC )T )TB WHERE PAGE = #{CURR_PAGE}

mapper.insert:
INSERT INTO TB_ILLUMI_MEET_DTL(
	MEET_SEQ 
  , NICK    
  , ADULT_CNT 
  , ELE_CNT 
  , INF_CNT 
  , TARGET_CNT 
  , DES_PLAN_AMT  
  , DES_NM  
  , DES_AMT 
  , DES_DATE  
  , DES_YN  
  , REFUND_YN  
  , REFUND_DATE 
  , ETC 
  , UPDDATE
)VALUES(
	#{MEET_SEQ} 
  , #{NICK}    
  , #{ADULT_CNT} 
  , #{ELE_CNT} 
  , #{INF_CNT} 
  , #{TARGET_CNT} 
  , #{DES_PLAN_AMT}  
  , #{DES_NM}  
  , #{DES_AMT}
  , #{DES_DATE}  
  , #{DES_YN}  
  , #{REFUND_YN} 
  , #{REFUND_DATE} 
  , #{ETC} 
  , NOW()
)

mapper.update:
UPDATE TB_ILLUMI_MEET_DTL SET
	NICK	 = #{NICK}    
  , ADULT_CNT 	 = #{ADULT_CNT}
  , ELE_CNT      = #{ELE_CNT}
  , INF_CNT      = #{INF_CNT}
  , TARGET_CNT   = #{TARGET_CNT}
  , DES_PLAN_AMT  = #{DES_PLAN_AMT}  
  , DES_NM       = #{DES_NM}
  , DES_AMT      = #{DES_AMT}
  , DES_DATE     = #{DES_DATE}
  , DES_YN       = #{DES_YN}
  , REFUND_YN    = #{REFUND_YN}
  , REFUND_DATE  = #{REFUND_DATE}
  , ETC          = #{ETC}
  , UPDDATE      = NOW()
WHERE MEET_DTL_SEQ = #{MEET_DTL_SEQ}
