mapper.select:
SELECT TB.* FROM( SELECT T.* , CAST(CEILING((RNUM)/#{PAGE_SIZE}) AS CHAR) AS PAGE , CAST(#{TOTAL_CNT} - RNUM +1 AS CHAR) AS RNUMUM FROM( SELECT MEM_SEQ , MEMBER_ID , MEMBER_NICK , MEMBER_NM , MEMBER_EMAIL , RET_YN , DATE_FORMAT(RET_DATE, '%Y-%m-%d')AS RET_DATE , DATE_FORMAT(REGDATE, '%Y-%m-%d')AS REGDATE , @RNUM := @RNUM + 1 AS RNUM FROM COM_USER_MEMBER ,(SELECT @RNUM := 0 ) STR WHERE 1 = 1
AND RET_YN = #{SCH_RET_YN}
ORDER BY DATE_FORMAT(REGDATE, '%Y-%m-%d %H:%i:%s') DESC )T )TB WHERE PAGE = #{CURR_PAGE}

