mapper.select:
SELECT COUNT(*) AS TOTAL_CNT FROM TB_WARRANT_INFO WHERE 1 = 1
AND DANZI = #{SCH_DANZI}
AND DONG = #{SCH_DONG}
AND WARRANT_YN = #{SCH_LEVEL}
AND AMT_YN = #{SCH_AMT_YN}
AND( USER_NM LIKE CONCAT('%', #{SCH_WORD},'%') OR USER_NM2 LIKE CONCAT('%', #{SCH_WORD},'%') OR NICK LIKE CONCAT('%', #{SCH_WORD},'%') OR HOSU LIKE CONCAT('%', #{SCH_WORD},'%') )

SELECT TB.* FROM( SELECT T.* , CEILING((RNUM)/#{PAGE_SIZE}) AS PAGE , CAST(#{TOTAL_CNT} - RNUM +1 AS CHAR) AS RNUMUM FROM( SELECT WARRANT_SEQ , DANZI , DONG , HOSU , USER_NM , NICK , HP , WARRANT_YN , CASE WHEN WARRANT_YN = 'Y' THEN '제출완료' ELSE '미제출' END AS WARRANT_YN_NM , AMT_YN , CASE WHEN AMT_YN = 'Y' THEN '납부완료' ELSE '미납' END AS AMT_YN_NM , ADDR , JOHAP_YN , CASE WHEN JOHAP_YN = 'Y' THEN '조합' WHEN JOHAP_YN = 'I' THEN '임대' ELSE '일반분양자' END AS JOHAP_YN_NM , ETC , DATE_FORMAT(REGDATE, '%Y-%m-%d %H:%i')AS REGDATE , HP2 , USER_NM2 , (SELECT GRADE FROM CAFE_MEMBER_INFO WHERE A.DONG = DONG AND A.HOSU = HOSU LIMIT 1)AS GRADE , @RNUM := @RNUM + 1 AS RNUM FROM TB_WARRANT_INFO A ,(SELECT @RNUM := 0 ) STR WHERE 1 = 1
AND DANZI = #{SCH_DANZI}
AND DONG = #{SCH_DONG}
AND WARRANT_YN = #{SCH_LEVEL}
AND AMT_YN = #{SCH_AMT_YN}
AND( USER_NM LIKE CONCAT('%', #{SCH_WORD},'%') OR USER_NM2 LIKE CONCAT('%', #{SCH_WORD},'%') OR NICK LIKE CONCAT('%', #{SCH_WORD},'%') OR HOSU LIKE CONCAT('%', #{SCH_WORD},'%') )
ORDER BY DANZI, CAST(DONG AS UNSIGNED),CAST(HOSU AS UNSIGNED) )T )TB WHERE PAGE = #{CURR_PAGE} ORDER BY DANZI, CAST(DONG AS UNSIGNED),CAST(HOSU AS UNSIGNED)

SELECT WARRANT_SEQ , CONCAT(DANZI, '단지') AS DANZI , CONCAT(DONG, '동') AS DONG , CONCAT(HOSU, '호') AS HOSU , USER_NM , NICK , HP , WARRANT_YN , CASE WHEN WARRANT_YN = 'Y' THEN '제출완료' ELSE '미제출' END AS WARRANT_YN_NM , AMT_YN , CASE WHEN AMT_YN = 'Y' THEN '납부완료' ELSE '미납' END AS AMT_YN_NM , ADDR , JOHAP_YN , CASE WHEN JOHAP_YN = 'Y' THEN '조합' WHEN JOHAP_YN = 'I' THEN '임대' ELSE '일반분양자' END AS JOHAP_YN_NM , ETC , DATE_FORMAT(REGDATE, '%Y-%m-%d %H:%i')AS REGDATE , HP2 , USER_NM2 FROM TB_WARRANT_INFO WHERE 1 = 1
AND DANZI = #{SCH_DANZI}
AND DONG = #{SCH_DONG}
AND WARRANT_YN = #{SCH_LEVEL}
AND( USER_NM LIKE CONCAT('%', #{SCH_WORD},'%') OR USER_NM2 LIKE CONCAT('%', #{SCH_WORD},'%') OR NICK LIKE CONCAT('%', #{SCH_WORD},'%') OR HOSU LIKE CONCAT('%', #{SCH_WORD},'%') )
ORDER BY DANZI, CAST(DONG AS UNSIGNED),CAST(HOSU AS UNSIGNED)

mapper.update:
UPDATE TB_WARRANT_INFO SET
	  USER_NM = #{USER_NM}
	, USER_NM2 = #{USER_NM2}
	, NICK = #{NICK}
	, HP = #{HP}
	, HP2 = #{HP2}
	, ADDR = #{ADDR}
	, WARRANT_YN = #{WARRANT_YN}
	, AMT_YN = #{AMT_YN}
	, ETC = #{ETC}
	, UPDDATE = NOW()
WHERE WARRANT_SEQ = #{WARRANT_SEQ}
